import { tv } from 'tailwind-variants';

const inputVariants = tv({
    base: "input",
    defaultVariants: {
        fullWidth: false,
        variant: "primary",
    },
    variants: {
        fullWidth: {
            false: "",
            true: "input--full-width",
        },
        variant: {
            primary: "input--primary",
            secondary: "input--secondary",
        },
    },
});

export { inputVariants };
